SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2021' AND "报告类型" = '季度报告' AND "报告期期末基金总份额" > "报告期期初基金总份额";

SELECT COUNT(*), SUM(机构投资者持有的基金份额)
FROM 基金份额持有人结构
WHERE 公告日期 = '2020' AND 机构投资者持有的基金份额占总份额比例 > 75;

SELECT COUNT(*), SUM(机构投资者持有的基金份额)
FROM 基金份额持有人结构
WHERE 公告日期 = '2019中期' AND 机构投资者持有的基金份额占总份额比例 > 75;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金份额持有人结构"
WHERE "公告日期" BETWEEN '20200401' AND '20200630' AND "机构投资者持有的基金份额" <> 0;

SELECT COUNT(DISTINCT "基金代码")
FROM "基金份额持有人结构"
WHERE "公告日期" >= '20190000' AND "公告日期" < '20200000' AND "基金管理人" = '富荣基金管理有限公司';

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金份额持有人结构"
WHERE "公告日期" BETWEEN '20190101' AND '20190331' AND "报告期基金总赎回份额" < 0;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"), 2)
FROM "基金份额持有人结构"
WHERE "公告日期" BETWEEN '20190401' AND '20190630' AND "机构投资者持有的基金份额" <> 0;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"), 2)
FROM "基金份额持有人结构"
WHERE "公告日期" BETWEEN '20210101' AND '20210331' AND "报告期基金总赎回份额" < 0;

SELECT COUNT(*), SUM(机构投资者持有的基金份额)
FROM 基金份额持有人结构
WHERE 公告日期 = '2020' AND 机构投资者持有的基金份额占总份额比例 > 30;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金份额持有人结构"
WHERE "公告日期" BETWEEN '20200101' AND '20200331' AND "赎回份额" < 0;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期期末基金总份额" - "报告期基金总赎回份额"), 2)
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2021' AND "报告类型" = '季度' AND "报告期" = '4';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '宏利基金管理有限公司' AND "成立日期" BETWEEN 20190000 AND 20199999;

SELECT COUNT(*), SUM(机构投资者持有的基金份额) as total_held_fund
FROM 基金份额持有人结构
WHERE 报告类型 = '中期报告' AND 截止日期 = '2019' AND 机构投资者持有的基金份额占总份额比例 > 50;
