SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金全称" LIKE '%嘉实基金管理有限公司%' AND "基金类型" = '其他型';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金代码" LIKE '南方基金管理股份有限公司%_%' AND "成立日期" LIKE '2019%';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金代码" LIKE '博时%_%' AND "成立日期" LIKE '2019%';

SELECT COUNT(*), SUM(机构投资者持有的基金份额)
FROM 基金份额持有人结构
WHERE 报告类型 = '年度报告' AND 截止日期 = '2019-12-31' AND 机构投资者持有的基金份额占总份额比例 > 0.66;

SELECT "A股公司行业划分表"."二级行业名称"
FROM "A股公司行业划分表"
WHERE "A股公司行业划分表"."股票代码" = '301096';

SELECT COUNT(DISTINCT "基金代码"), SUM("报告期基金总赎回份额") as "赎回份额"
FROM "基金规模变动表"
WHERE "公告日期" BETWEEN '20201001' AND '20201231' AND "报告期基金总赎回份额" < 0;

SELECT MAX("基金债券持仓明细"."持债市值") AS max_bond_value
FROM "基金债券持仓明细"
WHERE "基金代码" = '010005' AND "基金简称" = '鹏扬现金通利货币B' AND "报告类型" = '季报' AND "持仓日期" = '20210331' AND "债券类型" = '同业存单';
