SELECT AVG("A股票日行情表"."成交金额(元)") as "平均成交金额"
FROM "A股票日行情表"
WHERE "A股票日行情表"."股票代码" IN (SELECT "股票代码" FROM "A股公司行业划分表" WHERE "一级行业名称" = '仪器仪表' AND "交易日期" = '20191129');

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '债券型' AND "管理人" = '嘉实基金管理有限公司';

SELECT AVG("基金费率") AS "管理费率平均值"
FROM "基金基本信息"
WHERE "基金简称" LIKE '%红塔红土基金管理有限公司%' AND "成立日期" = '2019年';

SELECT ROUND((MAX("今开盘(元)") - MIN("最低价(元)")) / MIN("最低价(元)"), 3) AS "日价格振幅"
FROM "港股票日行情表"
WHERE "股票代码" = '00292' AND "交易日" = '20190502';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '混合型' AND "管理人" = '嘉实基金管理有限公司';

SELECT ROUND((MAX("今开盘(元)") - MIN("最低价(元)")) / MIN("最低价(元)") * 100, 3) AS "日价格振幅"
FROM "港股票日行情表"
WHERE "股票代码" = '01071' AND "交易日" = '20210715';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '股票型' AND "管理人" = '嘉实基金管理有限公司';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '混合型' AND "管理人" = '招商基金管理有限公司';

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '股票型' AND "管理人" = '招商基金管理有限公司';
