SELECT "基金债券持仓明细"."债券名称"
FROM "基金债券持仓明细"
WHERE "基金代码" = '易方达恒盛3个月定期开放混合' AND "持仓日期" = '20200331' AND ("基金债券持仓明细"."市值占基金资产净值比" >= (SELECT MAX("市值占基金资产净值比") FROM "基金债券持仓明细" WHERE "基金代码" = '易方达恒盛3个月定期开放混合' AND "持仓日期" = '20200331') - 0.01) ORDER BY "市值占基金资产净值比" DESC LIMIT 3;

