sql:
SELECT "基金股票持仓明细"."股票代码"
FROM "基金股票持仓明细"
WHERE "基金代码" = '富兰克林国海大中华精选混合(QDII)(美元现汇)' AND "报告类型" = '季报' AND "持仓日期" >= '20210101' AND "持仓日期" < '20210701' ORDER BY "市值" DESC LIMIT 1;

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金代码" LIKE '招商安庆债券%' AND YEAR("成立日期") = 2020;

SELECT COUNT(*)
FROM A股票日行情表
WHERE 交易日 = '20200204' AND A股票日行情表.最高价(元) > (SELECT MAX(A股公司行业划分表.最高价(元)) FROM A股公司行业划分表 WHERE A股公司行业划分表.股票代码 = A股票日行情表.股票代码);

SELECT COUNT(*)
FROM A股票日行情表
WHERE 交易日 = '20210121' AND 存在(SELECT * FROM A股票日行情表 AS b WHERE b.股票代码 = A股票日行情表.股票代码 AND b.最高价(元) > A股票日行情表.最高价(元));

SELECT DISTINCT "基金股票持仓明细"."行业名称"
FROM "基金股票持仓明细"
WHERE "基金股票持仓明细"."基金代码" = '中银证券价值精选灵活配置混合' AND "基金股票持仓明细"."持仓日期" = '20200331' AND "基金股票持仓明细"."所属证券市场" = '上海证券交易所' AND "基金股票持仓明细"."报告类型" = '季报';

SELECT "基金规模变动表"."报告期期末基金总份额" AS "资产净值", "基金规模变动表"."报告期期初基金总份额" AS "单位净值"
FROM "基金规模变动表"
WHERE "基金代码" = '006650' AND "公告日期" = '20200331';

SELECT "基金股票持仓明细"."股票代码"
FROM "基金股票持仓明细"
WHERE "基金代码" = '国泰金鹿混合' AND "报告类型" = '季报' AND "持仓日期" = '20200331' AND "基金股票持仓明细"."市值占基金资产净值比" = (SELECT MAX("基金股票持仓明细"."市值占基金资产净值比") FROM "基金股票持仓明细" WHERE "基金代码" = '国泰金鹿混合' AND "报告类型" = '季报' AND "持仓日期" = '20200331') - 0.0001;

SELECT DISTINCT "基金股票持仓明细"."行业分类"
FROM "基金股票持仓明细"
WHERE "基金股票持仓明细"."基金代码" = '鹏扬添利增强债券A' AND "基金股票持仓明细"."持仓日期" = '20211231' AND "基金股票持仓明细"."股票代码" LIKE '6%' OR "基金股票持仓明细"."股票代码" LIKE '0%' OR "基金股票持仓明细"."股票代码" LIKE '3%' OR "基金股票持仓明细"."股票代码" LIKE '9%';

SELECT DISTINCT "基金股票持仓明细"."所属国家(地区)", "基金股票持仓明细"."行业名称"
FROM "基金股票持仓明细"
WHERE "基金股票持仓明细"."基金代码" = '长盛竞争优势股票C' AND "基金股票持仓明细"."持仓日期" = '20201231' AND "基金股票持仓明细"."报告类型" = '季报';

SELECT "基金股票持仓明细"."股票代码"
FROM "基金股票持仓明细"
WHERE "基金代码" = '007484' AND "基金简称" = '嘉实动力先锋混合A' AND "报告类型" = '季报' AND "持仓日期" <= '20210930' ORDER BY "市值" DESC LIMIT 4;

SELECT COUNT(DISTINCT "基金代码")
FROM "基金持仓明细表"
WHERE "基金简称" LIKE '%永赢基金管理有限公司%' AND "债券类型" = '超短期融资券' AND "持仓日期" >= '2020-01-01' AND "持仓日期" < '2021-01-01';

SELECT COUNT(*)
FROM A股票日行情表
WHERE 交易日期 = '20190408' AND 最高价(元) > 上一交易日最高价;

SELECT "基金可转债持仓明细"."对应股票代码", "基金可转债持仓明细"."债券名称"
FROM "基金可转债持仓明细"
WHERE "基金代码" = '交银施罗德可转债债券A' AND "报告类型" = '基金定期报告' AND "持仓日期" = '20201231' AND "基金可转债持仓明细"."市值占基金资产净值比" = (SELECT MAX("基金可转债持仓明细"."市值占基金资产净值比") FROM "基金可转债持仓明细" WHERE "基金代码" = '交银施罗德可转债债券A' AND "报告类型" = '基金定期报告' AND "持仓日期" = '20201231') - 0.001;
