SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '债券型' AND "管理人" = '博时基金管理有限公司';

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2019' AND "报告类型" = '基金定期报告' AND "报告期期初基金总份额" <> "报告期期末基金总份额";

SELECT "A股公司行业划分表"."二级行业名称"
FROM "A股公司行业划分表"
WHERE "A股公司行业划分表"."股票代码" = '300321' AND "A股公司行业划分表"."交易日期" = (SELECT MAX("交易日期") FROM "A股公司行业划分表");

SELECT COUNT(*)
FROM "基金基本信息"
