SELECT COUNT(*)
FROM "基金规模变动表"
WHERE "报告期期末基金总份额" = 0;

SELECT COUNT(DISTINCT "基金代码"), SUM("报告期基金总申购份额") - SUM("报告期基金总赎回份额") as "净申购份额"
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2020' AND "报告类型" = '基金定期报告' AND ("报告期基金总申购份额" - "报告期基金总赎回份额") > 0;

SELECT COUNT(*)
FROM "基金规模变动表"
WHERE "报告期期末基金总份额" = 0;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2021' AND "报告类型" = '基金定期报告' AND "报告期期初基金总份额" <> "报告期期末基金总份额";

SELECT COUNT(DISTINCT "基金代码"), SUM("报告期基金总申购份额") - SUM("报告期基金总赎回份额") as "净申购份额"
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2021' AND "报告类型" = '基金定期报告' AND ("报告期基金总申购份额" - "报告期基金总赎回份额") > 0;

SELECT COUNT(DISTINCT "基金代码"), ROUND(SUM("报告期基金总赎回份额"),2)
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2019' AND "报告类型" = '基金定期报告' AND "报告期期末基金总份额" > "报告期期初基金总份额";

SELECT COUNT(DISTINCT "基金代码"), SUM("报告期基金总赎回份额") as "赎回份额"
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2021' AND "报告类型" = '基金定期报告' AND "报告期期末基金总份额" - "报告期期初基金总份额" < 0;

SELECT COUNT(DISTINCT "基金代码"), SUM("报告期基金总申购份额") - SUM("报告期基金总赎回份额") as "净申购份额"
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2021' AND "报告类型" = '基金定期报告' AND ("报告期期初基金总份额" - "报告期期末基金总份额") > 0;

SELECT COUNT(*)
FROM "基金基本信息"
WHERE "基金类型" = '股票型' AND "管理人" = '招商基金管理有限公司';

SELECT COUNT(DISTINCT "基金代码"), SUM("报告期基金总申购份额" - "报告期基金总赎回份额") as "净申购份额"
FROM "基金规模变动表"
WHERE "定期报告所属年度" = '2019' AND "报告类型" = '基金定期报告' AND ("报告期基金总申购份额" - "报告期基金总赎回份额") > 0;

